INSERT IGNORE INTO `#__translation_en` (`text`,`auto`,`imac`) VALUES ('Use this view to install packages.It can be widgets or even applications. There are three different ways to install a package.<br><ol><li><span style=\"text-decoration: underline;\">Install from package</span>: <span style=\"color: rgb(0, 102, 0); font-weight: bold;\">(Recommended)</span> Simply use the browse button to find your file on your computer and click install.</li><li><span style=\"text-decoration: underline;\">Install from an URL</span>: This can be an external URL. Enter the URL and then click install.</li><li><span style=\"text-decoration: underline;\">Install from a folder</span>: You might have the package extracted in a folder on your server , use this option to specify the folder and then click install. This option can be useful when the package is big and that your PHP upload limit is not enough or that your server times out.<br></li></ol>','1','1271818792LETO'),('Define which distribution server you want to use.<br>None: to disable updates and installs.<br>Production: <font color=\"#CC0000\">Recommended choice</font>. <br>Beta: to get latest beta releases.<br>Dev: <i>Not recommended, only use if advised by developers</i>.<br>','1','1271819434SRVJ'),('Script Type','1','1290670039OOGW'),('Your Cron Task on $HOMESITE is active and working!','1','1298294128HPXJ'),('The Cron Task on $HOMESITE could not be activated, check with support team...','1','1298294128HPXK'),('Package successfully installed','1','1298294132BJZM'),('Advance','1','1298294169DGIW'),('Total Apps','1','1298294174MWUX'),('Installed Apps','1','1298294174MWUY'),('Total No. of New Updated Apps','1','1298294174MWUZ'),('An unknown error happened while featching licenses.','1','1298350280PMMA'),('Apps Extra info','1','1298350283NKJB'),('Apps Level','1','1298350283NKJC'),('Apps Dependency','1','1298350283NKJD'),('Apps Information','1','1298350283NKJE'),('Apps Translation','1','1298350283NKJF'),('Apps Level Trans','1','129