function dBug_toggleRow(a){target=(document.all)?a.parentElement.cells[1]:a.parentNode.lastChild;dBug_toggleTarget(target,dBug_toggleSource(a))}function dBug_toggleSource(a){if(a.style.fontStyle=="italic"){a.style.fontStyle="normal";a.title="click to collapse";return"open"}else{a.style.fontStyle="italic";a.title="click to expand";return"closed"}}function dBug_toggleTarget(b,a){b.style.display=(a=="open")?"":"none"}function dBug_toggleTable(d){var b=dBug_toggleSource(d);if(document.all){var c=d.parentElement.parentElement;for(var a=1;a<c.rows.length;a++){target=c.rows[a];dBug_toggleTarget(target,b)}}else{var c=d.parentNode.parentNode;for(var a=1;a<c.childNodes.length;a++){target=c.childNodes[a];if(target.style){dBug_toggleTarget(target,b)}}}};