INSERT IGNORE INTO `#__translation_en` (`text`,`auto`,`imac`) VALUES ('Update Tips','1','1243943352MRHA'),('Updated From','1','1244107158CQZZ'),('<ul><li>Click on the delete button to delete currency conversion history by date specified.</li></ul><ul><li><span style=\"font-weight: bold;\">Updated</span> : the date the rates are updated.</li><li><span style=\"font-weight: bold;\">Updated From</span> : the source site where the updates are from.</li></ul><ul><li>Refresh your page after using delete, to see the changes.</li></ul>','1','1244107158CRAA'),('Currency Conversion History','1','1244112648DXEJ'),('curreny exchange site','1','1244112648DXEK'),('update currencies conversion exchange rates','1','1244195372FKZZ'),('Rates not found','1','1246516643YYN'),('Enter here the conversion fee. Take note that the rate is based on fee * exchange rate. Also take note that this fee is in percentage[ % ]. If this is leave to blank then the existing fee used in each currency\'s would be used or the fee set in the preference.<br>','1','1246516644MMDO'),('XML format','1','1246516644MMDP'),('See Format','1','1246516644MMDQ'),('Should have atleast 1 accepted currency remaining','1','1246699627QZRH'),('Not defined','1','1252894493BAJT'),('Problem in converting currency, please contact admin.','1','1257240515GXYD'),('<ul><li><span style=\"font-weight: bold;\">Update</span> : to update the latest exchange rates available.</li><li><span style=\"font-weight: bold;\">History </span>: to view the previous to current exchange rates. Note this is only available if \"Use History\" is set to Yes in the preference.<br></li></ul><ul><li><span style=\"font-weight: bold;\">New</span> : create/add new conversions.</li><li><span style=\"font-weight: bold;\">Edit</span> : edits the existing conversion.</li><li><span style=\"font-weight: bold;\">Delete </span>: deletes an existing conversion.</li></ul><ul><li>To edit you can simply click on its name or use the edit button.</li><li>To delete just simply click on the delete button.</li><li>Make sure that publish first the currencies that you want to update.</li><li>Refresh your page after every update done, to see changes.</li></ul>','1','1272517020GFKX'),('Title for your currency','1','1276181142EYWC'),('','1','1304525968YBL'),('Update Rates','1','1305714829NAVL'),('Multi-currencies is disabled for this site.','1','1319800425BXDU'),('No Currency Rate','1','1341596429OMYB'),('Name ( symbol )','1','1377828604HTRY'),('Name ( symbol ) ( code )','1','1377828604HTRZ'),('Name ( code )','1','1377828604HTSA'),('Name ( code ) ( symbol )','1','1377828604HTSB'),('Code ( symbol )','1','1377828604HTSC'),('Code ( symbol ) ( Name )','1','1377828604HTSD'),('Code ( Name )','1','1377828604HTSE'),('Code ( Name ) ( Symbol )','1','1377828604HTSF'),('( Symbol ) Name','1','1377828604HTSG'),('( Symbol ) Name ( Code )','1','1377828604HTSH'),('( Symbol ) Code','1','1377828604HTSI'),('( Symbol ) ( Code ) Name','1','1377828604HTSJ'),('Listing Type','1','1377828605EZSO'),('Filter list by','1','1377828605EZSP'),('Displayed Currency Format','1','1377828605EZSQ'),('Listing Type of Currencies','1','1377828605EZSR'),('Define what currency should be listed.<br>','1','1377828605EZSS'),('Currency module','1','1377828605EZST'),('Price widget','1','1379006374DSBA'),('Symbol ( $21 )','1','1380567464BZIB'),('Symbol & Code ( $21 USD )','1','1380567464BZIC'),('Code ( 21 USD )','1','1380567464BZID'),('Just price ( 21 )','1','1380567464BZIE'),('Enter the price you want to render.<br>','1','1380567465GYFW'),('Define','1','1380567465GYFX'),('Define the symbol and code used to display the price.<br>','1','1380567465GYFY'),('Decimal','1','1382809902KIWC'),('<div>This is to specify the format for decimal.</div>0: for no decimal<div>100: for 2 digits decimal</div><div><br></div>','1','1382809902KIWD'),('Use Style','1','1390568312ELWS'),('Define the number of decimal to display.<br><div>By default, the standard currency default decimal is used.</div>','1','13905683