INSERT IGNORE INTO `#__translation_en` (`text`,`auto`,`imac`) VALUES ('A ticket has been submitted for the category {widget:param|name=category}.\r\n\r\nTitle: {widget:param|name=title}.\r\n\r\nContent:\r\n\r\n{widget:param|name=description}\r\n\r\n\r\n{widget:param|name=link}','1','1380065050EKUC'),('A reply has been recorded for the ticket {widget:param|name=ticketid} (category {widget:param|name=category}.\r\n\r\nContent:\r\n\r\n{widget:param|name=description}\r\n\r\n\r\n{widget:param|name=link}','1','1380065050EKUD'),('Hello {widget:name},\r\n\r\nThis is an automated message, please do not reply. A reply has been recorded for your ticket {widget:param|name=ticketid}.\r\n\r\nLink:\r\n{widget:param|name=link}\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\nKind regards,\r\n{widget:sitename} Team','1','1380065050EKUE'),('Hi,\r\n\r\nYou still have ticket [{widget:param|name=ticketid}] which is still unsolved. If you want to close this ticket automatically, click this link {widget:param|name=linkclose}.\r\n\r\nTicket information\r\n\r\nTitle: {widget:param|name=title}\r\nCreated: {widget:param|name=created}\r\nBy: {widget:param|name=author}\r\nStatus: {widget:param|name=status}\r\n\r\nLast reply: {widget:param|name=lastreply}\r\n\r\nLink to view the Ticket:\r\n{widget:param|name=link}','1','1380065050EKUF'),('Hi,\r\n\r\nYour support is required to solve the ticket {widget:param|name=ticketid} of category {widget:param|name=category}. This ticket is assigned by {widget:param|name=assignedby}.\r\n\r\n\r\nTicket information:\r\n\r\nTitle: {widget:param|name=title}\r\nCreated: {widget:param|name=created}\r\n\r\n\r\nLink: {widget:param|name=link}\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\n\r\nThank you.\r\n\r\n','1','1380065050EKUG'),('Hello {widget:name}\r\n\r\nHereafter is a list of tickets which have been automatically closed due to lack of response.  If you consider the issue solved, you have nothing to do.\r\nHowever if the issue is not yet solved then you can re-open the ticket at any time.\r\n\r\n{widget:param|name=message}\r\n\r\nTicket information\r\n\r\nList of tickets:\r\n\r\n{widget:param|name=ticketids}','1','1380065050EKUH'),('Hello {widget:name}\r\n\r\nWe have not received any response back from you for ticket {widget:param|name=ticketid} in the last {widget:param|name=delaywarning} days, and would like to know if you consider this issue to be resolved.\r\n\r\nPlease note that the ticket will automatically be closed in {widget:param|name=closein} days if no response is received from you. The Ticket will not be closed in the case that you send another reply.\r\n\r\nLink:\r\n{widget:param|name=link}\r\n{widget:param|name=linkclose}\r\n\r\nTitle:\r\n{widget:param|name=title}\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\n\r\n\r\nPowered by Joobi','1','1380065050EKUI'),('Hi Support,\r\n\r\nUnpaid ticket has been submitted for the project  {widget:param|name=project}.\r\n\r\nTitle:\r\n{widget:param|name=title}\r\n\r\nLink:\r\n{widget:param|name=link}\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\nPlease don\'t reply though unless it is paid.\r\n\r\nThank You','1','1380065050EKUJ'),('Hello {widget:name},\r\n\r\nHere is a copy of your reply created for the Ticket: {widget:param|name=ticketid}.\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\n\r\nKind Regards,\r\n{widget:sitename} Team\r\n\r\n\r\n','1','1380065050EKUK'),('Hi {widget:name},\r\n\r\nThe ticket you are handling has been re-assigned to another moderator named, {widget:param|name=newmoderator}.\r\nTo view the ticket, follow this link {widget:param|name=link}.\r\n\r\nFor a brief summary, here\'s the content of the ticket:\r\nTitle:\r\n{widget:param|name=title}\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\n\r\n','1','1380065050EKUL'),('Hi {widget:name},\r\n\r\nThis is to inform you that the ticket which was assigned to you, a new reply has been recorded. To view the ticket, follow this link, {widget:param|name=link}.\r\n\r\nHere are the following information of the ticket:\r\n\r\nCategory Name:\r\n{widget:param|name=category}\r\n\r\nTicket ID:\r\n{widget:param|name=ticketid}\r\n\r\n\r\nContent:\r\n{widget:param|name=description}\r\n\r\n\r\n\r\n','1','1380065050EKUM'),('<p>Hello {widget:name},<br /><br />This is an automated message, please do not reply. This confirm that your ticket has been recorded.  The ticket number is {widget:param|name=ticketid} .<br /><br /><span style=\"font-style: italic; font-weight: bold; text-decoration: underline;\">Link:</span><br />{widget:param|name=link}<br /><br /><span style=\"font-style: italic; font-weight: bold; text-decoration: underline;\">Title:</span><br />{widget:param|name=title}<br /><br /><span style=\"font-weight: bold; font-style: italic; text-decoration: underline;\">Content:</span><br />{widget:param|name=description}<br /><br />Please be patient and do not submit multiple requests for the same question.<br /><br />Thank you.<br />{widget:sitename}</p>\r\n<p> </p>\r\n<p> </p>','1','1380065050EKUN'),('<p>Hello {widget:name}<br /><br />Hereafter is a list of tickets which have been automatically closed due to lack of response.  If you consider the issue solved, you have nothing to do.<br />However if the issue is not yet solved then you can re-open the ticket at any time.<br /><br />{widget:param|name=message}<br /><br /><span style=\"font-style: italic; font-weight: bold; text-decoration: underline;\">List of Tickets:</span><br /><br />{widget:param|name=ticketids}<br /><br /><br /></p>','1','1380065050EKUU'),('<p>Hello {widget:name},</p>\r\n<p> </p>\r\n<p>You have received a response for your ticket {widget:param|name=ticketid} and your credit had been deducted.</p>\r\n<p> </p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: bold; font-style: italic;\">Remaining Credits</span>:</span> {widget:param|name=credits}</p>\r\n<p> </p>\r\n<p>To Add more credits {widget:param|name=link2}</p>